package aa;

public class zjw1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(add(10,5));
		System.out.println(add1(10,5,6));
		System.out.println(dev(10,5));
		
		
	}

	//两个整数相加的方法
	public static int add(int a,int b) {
		return a+b;
	}
	//a=6 ;b=4;c=6
	public static int add1(int a,int b,int c) {
		return a+b+c ;
	}
	//两个整数相加
	public static int dev(int a,int b) {
		return a-b;
	}
	
	
	
	
	
}
